import React, { useState } from "react";
import boromir from "./boromir.png";
import song2 from "./song2.mp3";
import helena from "./Helena.mp3";
import ReactAudioPlayer from "react-audio-player";
//...

interface AudioPlayerProps {
  src: string[];
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({ src }) => {
  const [index, setIndex] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [audio, setAudio] = useState<HTMLAudioElement | null>(null);

  const handlePlayPause = () => {
    if (audio) {
      if (playing) {
        audio.pause();
      } else {
        audio.play();
      }
    }
  };

  const handleNext = () => {
    if (audio) {
      audio.pause();
      setIndex((index + 1) % src.length);
      setPlaying(false);
    }
  };

  const handlePrevious = () => {
    if (audio) {
      audio.pause();
      setIndex((index + src.length - 1) % src.length);
      setPlaying(false);
    }
  };

  return (
    <div>
      <audio
        src={src[index]}
        ref={(element) => {
          setAudio(element);
        }}
        onPlay={() => setPlaying(true)}
        onPause={() => setPlaying(false)}
      />
      <button onClick={handlePlayPause}>{playing ? "Pause" : "Play"}</button>
      <button onClick={handlePrevious}>Previous</button>
      <button onClick={handleNext}>Next</button>
    </div>
  );
};

function App() {
  const [imageClass, setImageClass] = useState("");

  return (
    <div className="container">
      <div style={{ display: "flex", justifyContent: "center" }}>
        {/* <h2 style={{ color: "darkblue" }}>boromir</h2> */}
        <img className={imageClass} style={{ width: "5%" }} src={boromir}></img>
      </div>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border: "1px solid black",
          width: "30%",
        }}
      >
        <div style={{ width: "50%" }}>
          <ol>
            <li>
              <div>novo jutro, novi dan</div>
            </li>
            <li>GLORIA</li>
            <li>novo jutro, novi dan</li>
          </ol>
        </div>

        <div style={{ display: "flex", flexDirection: "column", width: "50%" }}>
          <img style={{ width: "5%" }} src={boromir}></img>
          <AudioPlayer src={helena} />

          <ReactAudioPlayer
            onPlay={() => setImageClass("hoverImage")}
            src={helena}
            controls
          />
        </div>
      </div>
      <div>
        <ol>
          <p>+-âˆž</p>
        </ol>
      </div>
    </div>
  );
}

export default App;
