import React, { useState } from "react";
import { Song } from "../App";
import boromir from "../assets/images/boromir.png";
import boromir2 from "../assets/images/boromir2.png";
import boromir3 from "../assets/images/boromir3.png";
import song2 from "../assets/music/song2.mp3";
import song3 from "../assets/music/song3.mp3";
import helena from "../assets/music/Helena.mp3";
import { SongList } from "./SongList";
import { AudioPlayer } from "./AudioPlayer";

export const MainArea = () => {
  const [imageClass, setImageClass] = useState("");
  const [songClass, setSongClass] = useState("");
  const [image, setImage] = useState(0);
  const images = [boromir, boromir2, boromir3];

  let songs: Song[] = [
    { songFile: helena, image: boromir },
    { songFile: song2, image: boromir2 },
    { songFile: song3, image: boromir3 },
  ];

  console.log(songs[0]);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <img
          className={imageClass}
          style={{ width: "40%" }}
          src={images[image]}
        ></img>
        <AudioPlayer
          songs={songs}
          setSongClass={setSongClass}
          setImageClass={setImageClass}
        />
        <SongList songClass={songClass} />
      </div>
    </div>
  );
};
