import React, { useEffect, useState } from "react";
import boromir from "./boromir.png";
import boromir2 from "./boromir2.png";
import boromir3 from "./boromir3.png";
import song2 from "./song2.mp3";
import song3 from "./song3.mp3";
import helena from "./Helena.mp3";
import ReactAudioPlayer from "react-audio-player";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import { IconButton } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import { ArrowForward } from "@mui/icons-material";
//...

interface AudioPlayerProps {
  src: string[];
  setSongClass: any;
  setImageClass: any;
  setImage: any;
}

function BeatRotator({ bpm }: { bpm: number }) {
  const [rotation, setRotation] = useState(0);

  const rotationInterval = (60 / bpm) * 1000; // rotation interval in ms

  setInterval(() => {
    setRotation((prevRotation) => prevRotation + 1);
  }, rotationInterval);

  return (
    <div
      className="beat-rotator"
      style={{
        transform: `rotate(${rotation}deg)`,
        transition: `transform ${rotationInterval}ms linear`,
      }}
    >
      <p>hello</p>
    </div>
  );
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({
  src,
  setSongClass,
  setImageClass,
  setImage,
}) => {
  const [index, setIndex] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [audio, setAudio] = useState<HTMLAudioElement | null>(null);

  const images = [boromir, boromir2, boromir3];

  const handlePlayPause = () => {
    setSongClass("li-hover");
    setImageClass("hoverImage");
    setTimeout(() => {
      setImageClass("");
    }, 2000);
    if (audio) {
      if (playing) {
        audio.pause();
      } else {
        audio.play();
      }
    }
  };

  const handleNext = () => {
    if (audio) {
      audio.pause();
      setPlaying(true);
      setIndex((index + 1) % src.length);
      setImage((index + src.length - 1) % src.length);
      setPlaying(false);
    }
  };

  const handlePrevious = () => {
    if (audio) {
      audio.pause();
      setIndex((index + src.length - 1) % src.length);
      setImage((index + src.length - 1) % src.length);
      setPlaying(false);
    }
  };

  return (
    <div>
      <audio
        src={src[index]}
        ref={(element) => {
          setAudio(element);
        }}
        onPlay={() => setPlaying(true)}
        onPause={() => setPlaying(false)}
      />
      <div style={{ display: "flex", alignItems: "center" }}>
        <IconButton onClick={handlePrevious}>
          <ArrowBackIcon></ArrowBackIcon>
        </IconButton>
        <IconButton onClick={handlePlayPause}>
          {playing ? <PauseIcon /> : <PlayArrowIcon />}
        </IconButton>
        <IconButton onClick={handleNext}>
          <ArrowForwardIcon></ArrowForwardIcon>
        </IconButton>
      </div>
    </div>
  );
};

function App() {
  const [imageClass, setImageClass] = useState("");
  const [songClass, setSongClass] = useState("");
  const [image, setImage] = useState(0);

  const images = [boromir, boromir2, boromir3];

  return (
    <div className="container">
      <BeatRotator bpm={20} />
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        {/* <h2 style={{ color: "darkblue" }}>boromir</h2> */}
        <img
          className={imageClass}
          style={{ width: "20%" }}
          src={images[image]}
        ></img>
        <AudioPlayer
          src={[helena, song2, song3]}
          setSongClass={setSongClass}
          setImageClass={setImageClass}
          setImage={setImage}
        />
      </div>
      <div className="player-container">
        <div style={{ width: "50%" }}>
          <ol>
            <li className={songClass}>
              <div>NOVO JUTRO, NOVI DAN</div>
            </li>
            <li className={songClass}>GLORIA</li>
            <li className={songClass}>novo jutro, novi dan</li>
          </ol>
        </div>

        <div style={{ display: "flex", flexDirection: "column", width: "50%" }}>
          {/* <img style={{ width: "5%" }} src={boromir}></img> */}

          {/* 
          <ReactAudioPlayer
            onPlay={() => setImageClass("hoverImage")}
            src={helena}
            controls
          /> */}
        </div>
      </div>
      <div>
        <ol>
          <p>+-âˆž</p>
        </ol>
      </div>
    </div>
  );
}

export default App;
