import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Typography, Container, Grid, Box } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    background: "#F5F5F5",
    height: "100vh",
    padding: theme.spacing(4),
  },
  title: {
    fontFamily: "Montserrat",
    fontWeight: "700",
    fontSize: "2rem",
    marginBottom: theme.spacing(2),
    textAlign: "center",
    color: "#333",
  },
  subtitle: {
    fontFamily: "Rubik",
    fontWeight: "500",
    fontSize: "1.2rem",
    marginBottom: theme.spacing(4),
    textAlign: "center",
    color: "#333",
  },
  container: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  profileImage: {
    width: "20%",
    borderRadius: "50%",
    boxShadow: "0px 0px 10px 0px #333",
  },
}));

const BalenciagaFrontPage = () => {
  const classes = useStyles();

  return (
    <Box className={classes.root}>
      <Container>
        <Grid container>
          <Grid item xs={12}>
            <Typography className={classes.title}>
              Balenciaga Portfolio
            </Typography>
            <Typography className={classes.subtitle}>
              Welcome to my portfolio, showcasing my skills and experiences in
              fashion design.
            </Typography>
          </Grid>
          <Grid item xs={12} className={classes.container}>
            <img
              src="balenciaga-profile.jpg"
              className={classes.profileImage}
              alt="Balenciaga Profile"
            />
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

function App() {
  return <BalenciagaFrontPage />;
}

export default App;
