import React, { useState } from "react";
import boromir from "./assets/images/boromir.png";
import boromir2 from "./assets/images/boromir2.png";
import boromir3 from "./assets/images/boromir3.png";
import song2 from "./assets/music/song2.mp3";
import song3 from "./assets/music/song3.mp3";
import helena from "./assets/music/Helena.mp3";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import { IconButton } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

interface AudioPlayerProps {
  songs: Song[];
  setSongClass: any;
  setImageClass: any;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({
  songs,
  setSongClass,
  setImageClass,
}) => {
  const [index, setIndex] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [audio, setAudio] = useState<HTMLAudioElement | null>(null);

  const handlePlayPause = () => {
    setSongClass("li-hover");
    setImageClass("hoverImage");
    setTimeout(() => {
      setImageClass("");
    }, 2000);
    if (audio) {
      if (playing) {
        audio.pause();
      } else {
        audio.play();
      }
    }
  };

  const handleNext = () => {
    if (audio) {
      audio.pause();
      setPlaying(true);
      setIndex((index + 1) % songs.length);
      setPlaying(false);
    }
  };

  const handlePrevious = () => {
    if (audio) {
      audio.pause();
      setIndex((index + songs.length - 1) % songs.length);
      setPlaying(false);
    }
  };

  return (
    <div>
      <audio
        src={songs[index].songFile}
        ref={(element) => {
          setAudio(element);
        }}
        onPlay={() => setPlaying(true)}
        onPause={() => setPlaying(false)}
      />
      <div style={{ display: "flex", alignItems: "center" }}>
        <IconButton onClick={handlePrevious}>
          <ArrowBackIcon></ArrowBackIcon>
        </IconButton>
        <IconButton onClick={handlePlayPause}>
          {playing ? <PauseIcon /> : <PlayArrowIcon />}
        </IconButton>
        <IconButton onClick={handleNext}>
          <ArrowForwardIcon></ArrowForwardIcon>
        </IconButton>
      </div>
    </div>
  );
};

const SongList = ({ songClass }: { songClass: string }) => {
  return (
    <div className="song-list-container">
      <div style={{ width: "50%" }}>
        <ol>
          <li className={songClass}>
            <div>NOVO JUTRO, NOVI DAN</div>
          </li>
          <li className={songClass}>GLORIA</li>
          <li className={songClass}>novo jutro, novi dan</li>
        </ol>
      </div>

      <div
        style={{ display: "flex", flexDirection: "column", width: "50%" }}
      ></div>
    </div>
  );
};

interface Song {
  songFile: any;
  image: any;
}

const Footer = () => {
  return (
    <div>
      <ol>
        <p>+-∞</p>
      </ol>
    </div>
  );
};

function App() {
  return (
    <div className="container">
      <h1 style={{ color: "white" }}>BORIMIR</h1>
      <MainArea />
      <Footer />
    </div>
  );
}

export default App;
